import { createRouter, createWebHistory } from 'vue-router';
import IniciarSesion from '../components/IniciarSesion.vue';
import RecuperarContrasena from '../components/RecuperarContrasena.vue'; // Asegúrate de que la extensión sea .vue y la ruta sea correcta

const routes = [
  { path: '/', component: IniciarSesion }, // Ruta para iniciar sesión
  { path: '/recuperar-contrasena', component: RecuperarContrasena }, // Ruta para recuperar contraseña
];

const router = createRouter({
  history: createWebHistory(process.env.BASE_URL),
  routes,
});

export default router;
